/* eslint-disable react-hooks/exhaustive-deps */
import React, { useState, useEffect } from 'react';
import PoetryService from '../../../service/PoetryService'

import PoetryList from './PoetryList'
import { Form, Row } from 'react-bootstrap'

const Poetry = () => {

    const poetryService = new PoetryService()

    const [allPoetry, updateAllPoetry] = useState([])

    const loadPoetry = () => {
        poetryService
          .getAllPoetry()
          .then((response) => updateAllPoetry(response.data))
          .catch((err) => console.log(err));
    }

    useEffect(() => {
        loadPoetry()
    }, [updateAllPoetry])

    return (
      <>
        <Form.Group as={Row} controlId="formGridState">
          <Form.Label>Filtrar por tem√°tica</Form.Label>
          <Form.Control as="select" defaultValue="Choose...">
            <option>Escoja </option>
            <option>...</option>
          </Form.Control>
        </Form.Group>
        <PoetryList allPoetry={allPoetry} />
      </>
    );
}

export default Poetry;
