import React from "react";
import PoetryService from "../../../service/PoetryService";

import "./PoetryDetail.css";
import Button from 'react-bootstrap/Button'

const PoetryDetail = (props) => {

  console.log(props)

  const poetryService = new PoetryService()
  const { _id, title, body, handleClose } = props

  const deletePoetry = e => {

    e.preventDefault()

    poetryService
      .deleteOnePoetry(_id)
      .then(() => props.history.push("/poesias"))
      .catch((err) => console.log(err));
  }

  const modifyPoetry = () => {
    console.log('HOLA, MIRA COMO ME MODIFICO')
  }

  return (
    <>
      <h1>{title}</h1>
      <h2>{body}</h2>
      <p className="property">~Dioni</p>
      <div className="btn-detail" onClick={handleClose}>
        <Button className="card-btn">Cerrar</Button>
      </div>
      <Button onClick={modifyPoetry}>Editar</Button>
      <Button onClick={deletePoetry}>Borrar</Button>
    </>
  );
};

export default PoetryDetail;
