import React, { useState, useEffect } from 'react';
import './PoetryList.css'

import PoetryCard from './PoetryCard'
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import CardDeck from "react-bootstrap/CardDeck"

const PoetryList = (props) => {

  const { allPoetry, theme } = props;

  // const [actualpage, saveActualPage] = useState(1);
  // const [totalpages, saveTotalPages] = useState(1);

  const allPoetryFiltered = allPoetry.filter((elm) => elm.tags === theme);

  useEffect(() => {
    const cardsPerPage = 12;
    const calculateCardsPerPage = Math.ceil(
      (allPoetry.length - 1) / cardsPerPage
    );

    saveTotalPages(calculateCardsPerPage);

  }, [actualpage, allPoetry]); // DEPENDENCIA ACTUALPAGE

  //DEFINIR PAGINA ANTERIOR
  const previousPage = () => {

    const newActualPage = actualpage - 1;

    if (newActualPage === 0) return;

    saveActualPage(newActualPage);

  };

  //DEFINIR PAGINA SIGUIENTE
  const nextPage = () => {

    const newActualPage = actualpage + 1;

    if (newActualPage > totalpages) return;

    saveActualPage(newActualPage);

  };

  return (
    <Container className="list-container">
      {allPoetryFiltered.length < 1 || theme === "Escoja un tema" ? (
        <Row>
          <CardDeck className="list">
            {allPoetry.map((elm) => (
              <PoetryCard key={elm._id} {...elm} />
            ))}
          </CardDeck>
        </Row>
      ) : (
        <Row>
          <CardDeck className="list">
            {allPoetryFiltered.map((elm) => (
              <PoetryCard key={elm._id} {...elm} />
            ))}
          </CardDeck>
        </Row>
      )}

      {actualpage === 1 ? null : (
        <button
          type="button"
          className="bbtn btn-info mr-1"
          onClick={previousPage}
        >
          &laquo; Anterior
        </button>
      )}

      {actualpage === totalpages ? null : (
        <button
          type="button"
          className="bbtn btn-info mr-1"
          onClick={nextPage}
        >
          Siguiente &raquo;
        </button>
      )}
    </Container>
  );
}

export default PoetryList;
