import React, { Fragment } from 'react';
import AuthService from '../../service/AuthService'

import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import NavLink from "react-bootstrap/NavLink"


const Navigation = (props) => {

    const authService = new AuthService();

    const logout = () => {
      authService.logout()
        .then(() => {
          props.setTheUser(false);
        })
        .catch((err) => console.log(err));
    };

    return (
      // <Fragment>
      //   <Navbar bg="dark" variant="dark">
      //     <Navbar.Brand href="/">Las poesías de la abuela</Navbar.Brand>
      //     <Nav className="mr-auto">
      //       <Nav.Link href="/poesias">Poesías</Nav.Link>
      //       <Nav.Link href="/quien-soy">Sobre mí</Nav.Link>
      //     </Nav>
      //     <Nav>
      //       {props.loggedInUser ? (
      //         <>
      //           <Nav.Link href="/perfil">Administración</Nav.Link>
      //           <Nav.Link href="/" onClick={logout}>
      //             Cerrar sesión
      //           </Nav.Link>
      //         </>
      //       ) : (
      //         <>
      //           <Nav.Link href="/iniciar-sesion">Iniciar sesión</Nav.Link>
      //           <Nav.Link href="/registro">Registro</Nav.Link>
      //         </>
      //       )}
      //     </Nav>
      //   </Navbar>
      // </Fragment>

      <Navbar
        collapseOnSelect
        expand="lg"
        className="customNav "
        onToggle={this.setNavExpanded}
        expanded={this.state.navExpanded}
      >
        <Navbar.Brand href="#home">
          <NavLink to="/" className="brand" style={{ color: "black" }}>
            Car2Job
          </NavLink>
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="responsive-navbar-nav" />
        <Navbar.Collapse id="responsive-navbar-nav">
          <Nav className="mr-auto">
            <Nav.Link as="span">
              <NavLink
                onClick={this.setNavClose}
                exact
                to="/"
                exact
                style={{ color: "black" }}
              >
                Inicio
              </NavLink>
            </Nav.Link>
            {this.props.loggedInUser ? (
              <>
                {this.props.loggedInUser.role === "Pasajero" && (
                  <Nav.Link as="span">
                    <NavLink
                      onClick={this.setNavClose}
                      to="/mapa"
                      style={{ color: "black" }}
                    >
                      Crea tu ruta
                    </NavLink>
                  </Nav.Link>
                )}
                {this.props.loggedInUser.role === "Conductor" && (
                  <Nav.Link as="span">
                    <NavLink
                      onClick={setNavClose}
                      to="/lista-viajes"
                      style={{ color: "black" }}
                    >
                      Rutas
                    </NavLink>
                  </Nav.Link>
                )}
                <Nav.Link as="span">
                  <NavLink
                    onClick={setNavClose}
                    to="/perfil"
                    style={{ color: "black" }}
                  >
                    Perfil
                  </NavLink>
                </Nav.Link>
                <Nav.Link as="span">
                  <NavLink onClick={setNavClose} to="/">
                    <span onClick={logout} style={{ color: "black" }}>
                      Cerrar Sesión
                    </span>
                  </NavLink>
                </Nav.Link>
              </>
            ) : (
              <>
                <Nav.Link as="span">
                  <NavLink
                    onClick={this.setNavClose}
                    to="/login"
                    style={{ color: "black" }}
                  >
                    Inicio de sesión
                  </NavLink>
                </Nav.Link>
                <Nav.Link as="span">
                  <NavLink
                    onClick={this.setNavClose}
                    to="/signup"
                    style={{ color: "black" }}
                  >
                    Registro
                  </NavLink>
                </Nav.Link>
              </>
            )}
          </Nav>
        </Navbar.Collapse>
      </Navbar>
    );
}

export default Navigation;
