import React, { useState, useEffect } from 'react';
import PoetryService from '../../../service/PoetryService';

import './'

const PoetryDetail = (props) => {

    const poetryService = new PoetryService()

    const [poetryDetail, setPoetryDetail] = useState({});

    const id = (props.match.params.id)

    useEffect(() => {
      if (id) {
        poetryService
          .getOnePoetry(id)
          .then((response) => setPoetryDetail(response.data))
          .catch(err => console.log(err));
      }
      // eslint-disable-next-line
    }, [id])

    console.log(poetryDetail)
    const {title, body} = poetryDetail

    return ( 
        <>
        <h1>{title}</h1>
        <h2>{body}</h2>
        </>
     );
}

export default PoetryDetail;
