import React from 'react';
import AuthService from '../../service/AuthService'
import './Navbar.css'

import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";


const Navigation = (props) => {

    const authService = new AuthService();

    const logout = () => {
      authService.logout()
        .then(() => {
          props.setTheUser(false);
        })
        .catch((err) => console.log(err));
    };

    return (
      <Navbar
        className="navbar"
        collapseOnSelect
        expand="lg"
        // variant="dark"
      >
        <Navbar.Brand className="brand" href="/" id="nav">
          Las poesías de la abuela
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="responsive-navbar-nav" />
        <Navbar.Collapse id="responsive-navbar-nav">
          <Nav className="mr-auto">
            <Nav.Link class="links" href="/poesias">
              Poesías
            </Nav.Link>
            <Nav.Link class="links" href="/poesias">
              Poesías
            </Nav.Link>
            <Nav.Link class="links" href="/quien-soy">
              Sobre mí
            </Nav.Link>
          </Nav>

          {props.loggedInUser ? (
            <Nav>
              <Nav.Link class="links" href="/perfil">
                Administración
              </Nav.Link>
              <Nav.Link class="links" href="/" onClick={logout}>
                Cerrar sesión
              </Nav.Link>
            </Nav>
          ) : (
            <Nav>
              <Nav.Link class="links" href="/iniciar-sesion">
                Iniciar sesión
              </Nav.Link>
              <Nav.Link class="links" href="/registro">
                Registro
              </Nav.Link>
            </Nav>
          )}
        </Navbar.Collapse>
      </Navbar>
    );
}

export default Navigation;
