import React, { useState, useEffect } from "react";
import PoetryService from "../../../service/PoetryService";

const Summary = () => {

    const poetryService = new PoetryService();

    const [allPoetry, updateAllPoetry] = useState([]);

    const loadPoetry = () => {
      poetryService
        .getAllPoetry()
        .then((response) => updateAllPoetry(response.data))
        .catch((err) => console.log(err));
    };

    useEffect(() => {
      loadPoetry();
    }, [updateAllPoetry]);

    console.log(allPoetry)

    const loveArray = allPoetry.filter(elm => elm.tags === 'Amor')

    const natureArray = allPoetry.filter((elm) => elm.tags === "Naturaleza");

    const religionArray = allPoetry.filter((elm) => elm.tags === "Religi");


    return (
      <>
        <h1>ÍNDICE</h1>
        <div>
          <h2>Amor</h2>
        </div>
        <div>
          <h2>Naturaleza</h2>
        </div>
        <div>
          <h2>Religión</h2>
        </div>
        <div>
          <h2>Familia/Amistad</h2>
        </div>
        <div>
          <h2>Nostalgia</h2>
        </div>
        <div>
          <h2>Otros</h2>
        </div>
      </>
    );
}

export default Summary;
