import React, { useState, useEffect } from "react";
import PoetryService from "../../../service/PoetryService";

const Summary = () => {

    const poetryService = new PoetryService();

    const [allPoetry, updateAllPoetry] = useState([]);

    const loadPoetry = () => {
      poetryService
        .getAllPoetry()
        .then((response) => updateAllPoetry(response.data))
        .catch((err) => console.log(err));
    };

    useEffect(() => {
      loadPoetry();
    }, [updateAllPoetry]);

    objArray.sort(function (a, b) {
      return a.DepartmentName.localeCompare(b.DepartmentName);
    });


    const loveArray = allPoetry.filter(elm => elm.tags === 'Amor')

    const natureArray = allPoetry.filter((elm) => elm.tags === "Naturaleza");

    const religionArray = allPoetry.filter((elm) => elm.tags === "ReligiÃ³n");

    const familyFriendshipArray = allPoetry.filter((elm) => elm.tags === "Familia/Amistad");

    const othersArray = allPoetry.filter((elm) => elm.tags === "Otros");


    return (
      <>
        <h1>ÃNDICE</h1>
        <div>
          <h2>AMOR</h2>
          <div>
            {loveArray.map((elm) => (
              <h2>{elm.title}</h2>
            ))}
          </div>
        </div>
        <div>
          <h2>NATURALEZA</h2>
          <div>
            {natureArray.map((elm) => (
              <h2>{elm.title}</h2>
            ))}
          </div>
        </div>
        <div>
          <h2>RELIGIÃN</h2>
          <div>
            {religionArray.map((elm) => (
              <h2>{elm.title}</h2>
            ))}
          </div>
        </div>
        <div>
          <h2>FAMILIA/AMISTAD</h2>
          <div>
            {familyFriendshipArray.map((elm) => (
              <h2>{elm.title}</h2>
            ))}
          </div>
        </div>
        <div>
          <h2>Nostalgia</h2>
          <div>
            {loveArray.map((elm) => (
              <h2>{elm.title}</h2>
            ))}
          </div>
        </div>
        <div>
          <h2>OTROS</h2>
          <div>
            {othersArray.map((elm) => (
              <h2>{elm.title}</h2>
            ))}
          </div>
        </div>
      </>
    );
}

export default Summary;
