/* eslint-disable react-hooks/exhaustive-deps */
import React, { useState, useEffect } from "react";
import PoetryService from "../../../service/PoetryService";

import './Summary.css'
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from "react-bootstrap/Col";
import Modal from 'react-bootstrap/Modal' //Para modal
import PoetryDetail from '../Poetry/PoetryDetail' //Para modal

const Summary = (props) => {


    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const poetryService = new PoetryService();

    const [allPoetry, updateAllPoetry] = useState([]);

    //PARA PASAR PROPS DE LA POESIA
    const [selectedPoetry, updateSelectedPoetry] = useState([])

    const updatePoetry = (e)
    //

    const loadPoetry = () => {
      poetryService
        .getAllPoetry()
        .then((response) => updateAllPoetry(response.data))
        .catch((err) => console.log(err));
    };

    useEffect(() => {
      loadPoetry();
    }, [updateAllPoetry]);

    allPoetry.sort(function (a, b) {
      return a.title.localeCompare(b.title);
    });

    const loveArray = allPoetry.filter(elm => elm.tags === 'Amor')
    const natureArray = allPoetry.filter((elm) => elm.tags === "Naturaleza");
    const religionArray = allPoetry.filter((elm) => elm.tags === "Religión");
    const familyFriendshipArray = allPoetry.filter((elm) => elm.tags === "Familia/Amistad");
    const othersArray = allPoetry.filter((elm) => elm.tags === "Otros");


  return (
    <Container>
      <h1>ÍNDICE</h1>
      <div className="summary-general">
        <div className="summary-section">
          <h2>AMOR</h2>
          <Row md={4}>
            {loveArray.map((elm) => (
              <Col className="index-section">
                <p onClick={handleShow}>{elm.title.toUpperCase()}</p>
              </Col>
            ))}
          </Row>
        </div>
        <div className="summary-section">
          <h2>NATURALEZA</h2>
          <Row md={4}>
            {natureArray.map((elm) => (
              <Col className="index-section" onClick={handleShow}>
                <p>{elm.title.toUpperCase()}</p>
              </Col>
            ))}
          </Row>
        </div>
        <div className="summary-section">
          <h2>RELIGIÓN</h2>
          <Row md={4}>
            {religionArray.map((elm) => (
              <Col className="index-section" onClick={handleShow}>
                <p>{elm.title.toUpperCase()}</p>
              </Col>
            ))}
          </Row>
        </div>
        <div className="summary-section">
          <h2>FAMILIA/AMISTAD</h2>
          <Row md={4}>
            {familyFriendshipArray.map((elm) => (
              <Col className="index-section" onClick={handleShow}>
                <p>{elm.title.toUpperCase()}</p>
              </Col>
            ))}
          </Row>
        </div>
        <div className="summary-section">
          <h2>NOSTALGIA</h2>
          <Row md={4}>
            {loveArray.map((elm) => (
              <Col className="index-section" onClick={handleShow}>
                <p>{elm.title.toUpperCase()}</p>
              </Col>
            ))}
          </Row>
        </div>
        <div className="summary-section">
          <h2>OTROS</h2>
          <Row md={4}>
            {othersArray.map((elm) => (
              <Col className="index-section" onClick={handleShow}>
                <p>{elm.title.toUpperCase()}</p>
              </Col>
            ))}
          </Row>
        </div>

        <Modal show={show} onHide={handleClose}>
          <PoetryDetail {...props} handleClose={handleClose} />
        </Modal>
      </div>
    </Container>
  );
}

export default Summary;
