import React, { useState } from 'react';
import PoetryService from '../../../'

import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";

const Profile = (props) => {

  // const profileService = new ProfileService()

  const [poetry, updatePoetry] = useState({
    title: '',
    body: '',
    tags: ''
  })

  const { title, body, tags } = poetry

  // const [error, updateError] = useState({
  //   errorMessage: "",
  // });

  // const { errorMessage } = error;

  const handleInputChange = (e) => {
    updatePoetry({
      ...poetry,
      [e.target.name]: e.target.value,
    });
  }

  const handleFormSubmit = (e) => {
    e.preventDefault()

    // profileService
    //   .createPoetry(poetry)
    //   .then(() => props.history.push("/"))
    //   .catch((err) =>
    //     console.log(err)
    //   );

  }

    return (
      <Container as="main">
        <Row>
          <Col md={{ offset: 3, span: 6 }}>
            <Form onSubmit={handleFormSubmit}>
              <Form.Group controlId="formGroupEmail">
                <Form.Label>Título</Form.Label>
                <Form.Control
                  onChange={handleInputChange}
                  value={title}
                  name="title"
                  type="textl"
                  placeholder="Título"
                />
              </Form.Group>
              <Form.Group controlId="formGroupPassword">
                <Form.Label>Cuerpo del poema</Form.Label>
                <Form.Control
                  as="textarea"
                  onChange={handleInputChange}
                  value={body}
                  name="body"
                  type="textarea"
                  placeholder="Poema"
                />
              </Form.Group>
              <Form.Group>
                <Form.Label>Temática</Form.Label>
                <Form.Control
                  as="select"
                  onChange={handleInputChange}
                  value={tags}
                  name="tags"
                  type="text"
                >
                  <option>Seleccione temática del poema</option>
                  <option>Amor</option>
                  <option>Naturaleza</option>
                  <option>Religión</option>
                  <option>Familia/Amistad</option>
                  <option>Nostalgia</option>
                  <option>Otros</option>
                </Form.Control>
              </Form.Group>
              {/* {errorMessage && <p className="errorMessage">{errorMessage}</p>} */}
              <Button variant="dark" type="submit">
                Enviar
              </Button>
            </Form>
          </Col>
        </Row>
      </Container>
    );
}

export default Profile;
