/* eslint-disable react-hooks/exhaustive-deps */
import React, { useState, useEffect } from "react";
import PoetryService from "../../../service/PoetryService";

import Modal from 'react-bootstrap/Modal' //Para modal
import PoetryDetailIndex from '../Poetry/PoetryDetailIndex' //Para modal

const Summary = (props) => {


    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const poetryService = new PoetryService();

    const [allPoetry, updateAllPoetry] = useState([]);

    const loadPoetry = () => {
      poetryService
        .getAllPoetry()
        .then((response) => updateAllPoetry(response.data))
        .catch((err) => console.log(err));
    };

    useEffect(() => {
      loadPoetry();
    }, [updateAllPoetry]);

    allPoetry.sort(function (a, b) {
      return a.title.localeCompare(b.title);
    });

    const loveArray = allPoetry.filter(elm => elm.tags === 'Amor')
    const natureArray = allPoetry.filter((elm) => elm.tags === "Naturaleza");
    const religionArray = allPoetry.filter((elm) => elm.tags === "Religión");
    const familyFriendshipArray = allPoetry.filter((elm) => elm.tags === "Familia/Amistad");
    const othersArray = allPoetry.filter((elm) => elm.tags === "Otros");


    return (
      <>
        <h1>ÍNDICE</h1>
        <div>
          <h2>AMOR</h2>
          <div>
            {loveArray.map((elm) => (
              <div onClick={handleShow}>
                <h2>{elm.title}</h2>
              </div>
            ))}
          </div>
        </div>
        <div>
          <h2>NATURALEZA</h2>
          <div>
            {natureArray.map((elm) => (
              <h2>{elm.title}</h2>
            ))}
          </div>
        </div>
        <div>
          <h2>RELIGIÓN</h2>
          <div>
            {religionArray.map((elm) => (
              <h2>{elm.title}</h2>
            ))}
          </div>
        </div>
        <div>
          <h2>FAMILIA/AMISTAD</h2>
          <div>
            {familyFriendshipArray.map((elm) => (
              <h2>{elm.title}</h2>
            ))}
          </div>
        </div>
        <div>
          <h2>Nostalgia</h2>
          <div>
            {loveArray.map((elm) => (
              <h2>{elm.title}</h2>
            ))}
          </div>
        </div>
        <div>
          <h2>OTROS</h2>
          <div>
            {othersArray.map((elm) => (
              <h2>{elm.title}</h2>
            ))}
          </div>
        </div>

        <Modal show={show} onHide={handleClose}>
          <PoetryDetailIndex {...props} handleClose={handleClose} />
        </Modal>
      </>
    );
}

export default Summary;
